metadata.code:
const items = Inputs.Items || [];
let totalPrice = 0;

// Loop through each item in the array
for (let i = 0; i < items.length; i++) {
  // Check if the item has a price and count property
  if ('price' in items[i] && 'count' in items[i]) {
    // Multiply the price and count of each item and add it to the total price
    totalPrice += items[i].price * items[i].count;
  } else {
    // If an item doesn't have a price or count property, throw an error
    throw new Error('Invalid item in array');
  }
}

// Output the total price
Outputs.TotalPrice = totalPrice;
Outputs.Success();

